#!/bin/bash\n\n# Deploy script for AI Core system\n\necho "Starting deployment of AI Core..."\n\n# Build the configuration\npython -m core.config.merged merged_configuration.py\n\n# Run tests\npython -m unittest discover -s tests/unit \npython -m unittest discover -s tests/integration\n\n# Start services in order\necho "Starting gateway service..."\npython -m core.gateway.main\n\necho "Starting kernel and planner services..."\npython -m core.kernel.main\n\necho "Deployment completed successfully."